import axios from 'axios';

function getAdvertissement() {
    return axios.get('http://localhost:8080/api/advertissements');
}

function getAdvertissementById(id) {
    return axios.get(`http://localhost:8080/api/advertissements/${id}`);
}

function createAdvertissement(advertissement) {
    return axios.post('http://localhost:8080/api/advertissements', advertissement);
}

function updateAdvertissement(advertissement) {
    return axios.put(`http://localhost:8080/api/advertissements/`, advertissement);
}

function deleteAdvertissement(advertissement) {
    return axios.delete(`http://localhost:8080/api/advertissements/`, advertissement);
}

const AdvertissementService = {
        getAdvertissement,
        getAdvertissementById,
        createAdvertissement,
        updateAdvertissement,
        deleteAdvertissement
}

export default AdvertissementService;
