import axios from 'axios';

function getCompanies() {
    return axios.get('http://localhost:8080/api/companies');
}

function getCompanyById(id) {
    return axios.get(`http://localhost:8080/api/companies/${id}`);
}

function createCompany(company) {
    return axios.post('http://localhost:8080/api/companies', company);
}

function updateCompany(company) {
    return axios.put(`http://localhost:8080/api/companies/`, company);
}

function deleteCompany(id) {
    return axios.delete(`http://localhost:8080/api/companies/${id}`);
}

const CompaniesService = {
        getCompanies,
        getCompanyById,
        createCompany,
        updateCompany,
    deleteCompany
};

export default CompaniesService;
